package Day0323hw;

public class P66 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 0;
		int j;
			
		for ( i = 0; i<5; i++)
		{ 
			for (j = 0; j<i+1; j++ )	// i는 행, j는 열, i와 j의 관계 파악
			{
			
				System.out.print("*");
				
			}
			
			System.out.println();
			
			
			
		}
		
	}

}
