
public class AppleLemon {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//배열 2차원 사용해서 값 담기
				// a p p l e
				// l e m o n
		
		char[][] AL;
		AL = new char[2][5];
				
		AL[0][0] = 'a';
		AL[0][1] = 'p';
		
		AL[1][0] = 'l';
		
		
		
		System.out.println(AL[0][0]);
		System.out.println(AL[1][0]);
		
	}

}
